      },
      "transform": [
        {"filter": "datum.Year == 2020"}
      ],
      "mark": "bar",
      "encoding": {
        "x": {
          "field": "Adoption Rate",
          "type": "quantitative"
        },
        "y": {
          "field": "Technology Type",
          "type": "nominal",
          "sort": "-x"
        }
      }
    };

    vegaEmbed('#vis', spec);
  </script>
</body>
</html>
