                const chartData = {};
                csvData.forEach(row => {
                    const [month, sector, energyConsumed] = row.split(',');
                    if (month === 'January') {
                        if (!chartData[sector]) {
                            chartData[sector] = 0;
                        }
                        chartData[sector] += parseFloat(energyConsumed);
                    }
                });
                const sectors = Object.keys(chartData);
                const energyConsumedValues = Object.values(chartData);
                const chart = echarts.init(document.getElementById('chart'));
                chart.setOption({
                    title: {
                        text: 'Energy Consumed for each Energy Sector in January'
                    },
                    tooltip: {
                        trigger: 'axis',
                        axisPointer: {
                            type: 'shadow'
                        }
                    },
                    grid: {
                        left: '3%',
                        right: '4%',
                        bottom: '3%',
                        containLabel: true
                    },
                    xAxis: {
                        type: 'value'
                    },
                    yAxis: {
                        type: 'category',
                        data: sectors
                    },
                    series: [
                        {
                            name: 'Energy Consumed',
                            type: 'bar',
                            data: energyConsumedValues
                        }
                    ]
                });
            });
    </script>
</body>
</html>
